import React, { useState, useEffect} from 'react';
import axios from 'axios';

const App = () => {
  const [temprature, setTemprature] = useState(null);
  const [ error, setError] = useState(null);

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        const apiKey = "236becae49a1c0080a598c5fd17d8d4b";
        const city = "Lahore";
        const country = "PK";
        const url = "https://api.openweathermap.org/data/2.5/weather?q=lahore,pk&appid=236becae49a1c0080a598c5fd17d8d4b&units=metric";

        const response = await axios.get(url);
        setTemprature(response.data.clouds.all);
        }
        catch (err) {
          setError("Failed to fetch weather data");
          console.error(err);
        }
      };
