}, []);

return (
  <div style={{ fontFamily: "Arial", textAlign: "center", marginTop: "50px"  }}>
  <h1>Weather in Lahore, Pakistan</h1>
  {error ? (
    <p style={{ color: "red" }}>{error}</p>
  ) : temprature !== null ?(
    <p> The Current Humidity is <strong>{temprature}</strong></p>
  ) : (
    <p>Loading...</p>
  )}
  </div>
);
};


export default App;
